INSERT INTO `resources` VALUES(59, 'Educator Voice: Changing the World One Student at a Time: Principal Jose Navarro ', 11, 'http://bcove.me/gb0n54i1', NULL, 'Jose Navarro, Principal at Social Justice Humanitas Academy in San Fernando, CA, discusses the impact that Facing History has had on the students at his school.', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2839175471001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2839175471001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="486" height="537" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2839175471001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2839175471001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="486" height="537" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>');
INSERT INTO `resources` VALUES(60, 'Scholar Voice: Martha Minow, Professor at Harvard Law School', 11, 'http://bcove.me/evvfhanx', NULL, 'Martha Minow, Jeremiah Smith, Jr., Professor of Law, Harvard Law School, discusses Facing History and Ourselves.', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2816509145001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2816509145001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="486" height="537" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2816509145001&playerID=1618550244001&player