INSERT INTO `resources` VALUES(62, 'Facing History Staff Voice: Margot Stern-Strom', 11, 'http://bcove.me/2hjq3lo0', NULL, 'Margot Stern-Strom,\r\nExecutive Director of Facing History and Ourselves', '', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2814744646001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2814744646001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="486" height="537" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>');
INSERT INTO `resources` VALUES(63, 'Creating a Reflective Classroom Community', 11, 'http://bcove.me/9x2bg8fn', '', 'Doc Miller explains the importance of creating a reflective classroom.', '', '<object id="flashObj" width="486" height="537" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2589528788001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2589528788001&playerID=1618550244001&playerKey=AQ~~,AAABYEdBaAk~,1dGnVytVBSxfjd3-9uew3O6eYAhpizaC&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="486" height="537" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>');
INSERT INTO `resources` VALUES(64, 'Facing History and Ourselves: Creating a Reflective Classroom Community', 9, 'https://drive.google.com/file/d/0B9E6hxuwgtz-Y2M5QXowUzl4U2s/edit?usp=sharing', '', 'This paper is one of a series of pedagogical briefs written to illuminate the work of Facing History and Ourselves from theory to practice. This resource focuses on the process of creating a reflective classroom community.', '', NULL);
INSERT INTO `resources` VALUES(65, 'Communicating Online: Discussion Guidelines', 10, '', NULL, 'Discussions may be difficult, but they also present opportunities for us and our students to more deeply develop self-awareness and consider different perspectives about race and identity. Having these discussions online, rather than face to face, adds another layer of complexity that we must consider together.', '', '<h2>Communicating Online</h2>\r\n<p>Discussions may be difficult, but they also present opportunities for us and our students to more deeply develop self-awareness and consider different perspectives about race and identity. Having these discussions online, rather than face to face, adds another layer of complexity that we must consider together.</p>\r\n<hr />\r\n<p>Here are some tips and guidelines for communicating in online discussions:</p>\r\n<p></p>\r\n<p></p>\r\n<h3>The Way You Communicate Affects All Participants:</h3>\r\n<p>As a participant, you are a member of an online community. Your postings can help push the group to think in new directions and create a sense of community, or they can be divisive. Try to be thoughtful and sensitive in your tone. Be cautious with sarcasm and humor online, as they often don''t come across as intended. As in a face-to-face group conversation, please make a special effort to â€œlistenâ€ carefully to other participants, and as a "critical friend," post your responses kindly and thoughtfully.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Let Yourself Be Heard:</h3>\r\n<p>Don''t be afraid to speak your thoughts. Your perspective, whether it is similar to that of others in the group or different, is important.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Join the conversation.</h3>\r\n<p>Remember, the facilitator and other participants can''t read body language online to know if you''re engaged in the conversation and thinking about the content. We need to "hear" you.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Be Careful Not to Dominate the Discussion:</h3>\r\n<p>Every online group has leaders, and leaders are great. But try to be cautious about overwhelming the conversation. Share your thoughts and responses, but make sure you give room for others to respond, and that you''re letting the facilitator do his/her job as well.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Encouragement Goes a Long Way:</h3>\r\n<p>Comments in the discussion can be either substantial, thought-provoking reflections or simple words of encouragement to other participants. They can push other participants to think further, or simply just say "Nicely put," or "I agree."</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Brevity!</h3>\r\n<p>Everyone already has a lot to read. As you''re writing your messages, remember that we want to hear what you have to say but if it takes you too long to say it, other participants might not be able to read the whole thing.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Patience:</h3>\r\n<p>Online asynchronous (i.e., not at the same time) discussion is a new mode of communication for most of us. There will be times when you post to a discussion and no one responds to that post, or a response comes many hours or even days later. This is the nature of an online discussion. Know that even though your post may not get a response, you can rest assured that many participants have read the post.</p>\r\n<p></p>\r\n<hr />\r\n<p></p>\r\n<h3>Email vs. Discussion Board:</h3>\r\n<p>As a general rule, use the Discussions for all workshop communication. Email should only be used to share an issue of concern or private matter with the workshop facilitators or another participant. Email should also be used to communicate about issues unrelated to specific workshop content.</p>');
INSERT INTO `resources` VALUES(66, 'Facing History and Ourselves Online Courses: Updating Your Profile', 11, 'http://vimeo.com/75602029', NULL, 'This video will walk you through the steps to creating and updating your user profile on the Moodle Learning Management System (LMS), the learning platform used by Facing History and Ourselves for online courses.', '', '<iframe src="//player.vimeo.com/video/75602029" width="500" height="400" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>');
INSERT INTO `resources` VALUES(67, 'Bubbl.us', 12, 'http://bubbl.us', NULL, 'This tool allows for the creation and export of mind maps. You can create an identity chart on Bubbl.us, then export it as a .jpg or a .png file.', '', '');
INSERT INTO `resources` VALUES(68, 'Wordle.net', 12, 'http://wordle.net', NULL, 'Create your identity chart as a word cloud! Word clouds give greater prominence to words that appear more often within a text selection. Here''s a suggestion: compose a short biography of yourself, then paste it into the Wordle and see what comes up!', '', '');
INSERT INTO `resources` VALUES(69, 'Prezi', 12, 'http://www.prezi.com', NULL, 'Prezis are great alternatives to traditional slideshows. The site has some useful tutorials on how to best create a Prezi. Keep in mind that you will need to create an account with a login for this option.\r\nPowerPoint: Using Microsoft PowerPoint, create your identity chart with a slide, or create a full slideshow that you can share with your classmates.', '', '');
INSERT INTO `resources` VALUES(70, 'Teaching Strategy: Identity Charts', 14, 'https://www.facinghistory.org/for-educators/educator-resources/teaching-strategies/identity-charts', NULL, 'Identity charts are a graphic tool that helps students consider the many factors that shape who we are as individuals and as communities. They can be used to deepen studentsâ€™ understanding of themselves, groups, nations and historical and literary figures. Sharing their own Identity charts with peers can help students build relationships and breakdown stereotypes. In this way, identity charts can be utilized as an effective classroom community-building tool.', '', '');
INSERT INTO `resources` VALUES(71, 'Facing History and Ourselves Online Courses: Educator Resources Page', 11, 'http://vimeo.com/86609495', NULL, 'This video will show you how to negotiate your way around the Educator Resources section of the Facing History and Ourselves website.', '', '<iframe src="//player.vimeo.com/video/86609495" width="500" height="400" webkitallowfullscreen mozallowfullscreen allowfullscreen></iframe>');
