INSERT INTO `resources` VALUES(100, 'Letter from Daisy Bates to Roy Wilkins, 12/17/1957', 10, 'http://memory.loc.gov/mss/mssmisc/ody/ody0918a/0918a001v.jpg', '', 'Arkansas-born Daisy Bates worked as a crusading newspaper owner-journalist, becoming president of the Arkansas NAACP. This letter from Daisy Bates to Roy Wilkins, dated December 17, 1957, discusses the treatment of the Little Rock Nine.', '', NULL);
