INSERT INTO `resources` VALUES(111, 'Student Voice: "This is a Siren": Student and Poet Jonathan Lykes', 11, '', '3024552493001', 'Facing History and Ourselves alumnus Jonathan Lykes delivers a poetic, emotional account of the meaning Facing History and Ourselves holds within his own journey from city school to University of Chicago.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience3024552493001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="3024552493001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="3024552493001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(112, 'Facing History Staff Voice: A Letter to Teachers: This is Facing History', 11, '', '2814744646001', 'In their own words, students, teachers, principals, and founder Margot Stern Strom tell the story of how the Facing History experience expands leadership skills, empathy, and moral insight.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience2814744646001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="2814744646001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="2814744646001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(113, 'Padlet Wall: What is Facing History?', 12, 'http://padlet.com/wall/s9xl8a16qmo6', '', 'Padlet is virtual cork board. Participants can post notes, video, links, and other multimedia items in a draggable, easily digestible format. It''s an excellent tool for discussing ideas in a unique way, and in many ways it mimic''s one of Facing History''s favorite teaching techniques: big paper. ', '', NULL);
