INSERT INTO `resources` VALUES(132, 'Foreword to A Convenient Hatred', 9, 'https://www.facinghistory.org/sites/default/files/Foreword.pdf', '', 'This forward  to <em>A Convenient Hatred</em> was written by Sir Harold Evans, editor-at-large of Reuters and author of two critically acclaimed best-selling histories of America: <em>The American Century</em> and <em>They Made America</em>.', '', NULL);
INSERT INTO `resources` VALUES(133, 'The Power of a Lie: The History of the Blood Libel', 11, NULL, '2193237308001', 'In this video Facing History curriculum developers Phyllis Goldstein, Jeremy Nesoff, Shira Deener, and Doc Miller set the record straight about the false claim that Jews engaged in ritual murder and blood letting. Traced back to Norwich, England in the 12th century when Europe was filled with fear and anxiety, this myth provided a convenient rationale for Christians to scapegoat Jews.  The myth and lie of the blood libel has had enduring power, and crops up periodically during periods of social or economic unrest, and on all continents across the world. This is why it is particularly important for educators to teach about the blood libel, to ensure that future generations will not accept this lie as truth.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\n\n<div style="display:none">\n\n</div>\n\n<!--\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \n-->\n\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\n\n<object id="myExperience2193237308001 " class="BrightcoveExperience">\n  <param name="bgcolor" value="#FFFFFF" />\n  <param name="width" value="560" />\n  <param name="height" value="315" />\n  <param name="playerID" value="2193237308001 " />\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\n  <param name="isVid" value="true" />\n  <param name="isUI" value="true" />\n  <param name="dynamicStreaming" value="true" />\n  \n  <param name="@videoPlayer" value="2193237308001 " />\n</object>\n\n<!-- \nThis script tag will cause the Brightcove Players defined above it to be created as soon\nas the line is read by the browser. If you wish to have the player instantiated only after\nthe rest of the HTML is processed and the page load is complete, remove the line.\n-->\n<script type="text/javascript">brightcove.createExperiences();</script>\n\n<!-- End of Brightcove Player -->');
