INSERT INTO `resources` VALUES(144, 'Identity Charts ', 14, 'https://www.facinghistory.org/for-educators/educator-resources/teaching-strategies/identity-charts', '', 'Identity charts are a graphic tool that helps students consider the many factors that shape who we are as individuals and as communities. They can be used to deepen studentsâ€™ understanding of themselves, groups, nations and historical and literary figures. Sharing their own identity charts with peers can help students build relationships and breakdown stereotypes. In this way, identity charts can be utilized as an effective classroom community-building tool.', '', NULL);
INSERT INTO `resources` VALUES(145, '"The Most American Thing You Can Do" by Eboo Patel ', 10, 'https://www.dropbox.com/s/theigi1qgje3bi1/The%20Most%20American%20Thing%20You%20Can%20Do%20-%20Eboo%20Patel.docx', '', 'In this essay adapted from <i>Sacred Ground: Pluralism, Prejudice, and the Promise of America,</i> Patel focuses on the controversy over the potential establishment of Cordoba House near Ground Zero. New York City mayor Michael Bloomberg not only supports the Cordoba House but also rises as a hero in the battle for pluralism. By referring to historical individuals and documents, Patel proves that pluralism is rooted in the founding moments of America and that just like Bloomberg, we can work to defeat prejudice in order to create a more loving, welcoming and respectful society. ', '', NULL);
