INSERT INTO `resources` VALUES(165, 'Immigration in Today''s World', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=2816174406001', '2816174406001', 'Listen as scholars Marcelo SuÃƒÂ¡rez-Orozco, John R. Bowen, and Sir Keith Ajegbo outline different circumstances, histories, and reasons for immigration across countries.\r\n\r\n<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience2816174406001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="2816174406001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="2816174406001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience2816174406001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="2816174406001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="2816174406001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(166, 'Secularity and Religious Identity: The French Headscarf Dilemma', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=2816202899001', '2816202899001', 'John R. Bowen, Patrick Weil, Jacqueline Bhabha, Sir Keith Ajegbo, and Maleiha Malik explain the history of secularity (laÃƒÂ¯cite) in France, where it is the State''s role to protect individual freedom of conscience and respect of all faiths by keeping religion out of the public sphere. This tradition underlies the banning of head scarves in public schools in 2004. The issue has not played out the same way in England, where there is a history of informal negotiation of the rights of minorities. The speakers were interviewed as part of the 2008 Facing History and Ourselves/Harvard Law School conference entitled Hope, Critique, and Possibility: Universal Rights in Societies of Difference.\r\n\r\n\r\n<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience2816202899001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="2816202899001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="2816202899001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience2816202899001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="2816202899001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="2816202899001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(167, 'Sholem Aleichem: Identity in a Changing World', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=3541647634001', '3541647634001', 'Focusing on one of Sholem AleichemÃ¢Â€Â™s later stories, "On Account of a Hat" (1913), this excerpt from "Sholem Aleichem: Laughing in the Darkness" addresses the tension between constructing an identity for oneself that is both unique and modern and still playing homage to one''Â€Â™s ancestry.\r\n\r\n\r\n<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience3541647634001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="3541647634001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="3541647634001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->\r\n\r\n', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience3541647634001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="3541647634001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="3541647634001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
