INSERT INTO `resources` VALUES(172, 'Julius Lester: Finding One''s Voice', 10, '', '', 'Julius Lester, a noted author and college professor, describes how discovering an ancient form of poetry helped him find his voice and define his own identity despite the limitations society imposed on him.', '', NULL);
INSERT INTO `resources` VALUES(174, 'Little Things Are Big -- Jesus Colon', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=2850008634001', '2850008634001', 'In the 1950s, segregation and ideas about "race" shaped the way Americans in all parts of the nation saw one another as well as the way they saw themselves. As writer Jesus Colon discovered on a subway ride in New York City, those ideas also influenced the decisions people made about one another.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\n\n<div style="display:none">\n\n</div>\n\n<!--\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \n-->\n\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\n\n<object id="myExperience2850008634001 " class="BrightcoveExperience">\n  <param name="bgcolor" value="#FFFFFF" />\n  <param name="width" value="560" />\n  <param name="height" value="315" />\n  <param name="playerID" value="2850008634001 " />\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\n  <param name="isVid" value="true" />\n  <param name="isUI" value="true" />\n  <param name="dynamicStreaming" value="true" />\n  \n  <param name="@videoPlayer" value="2850008634001 " />\n</object>\n\n<!-- \nThis script tag will cause the Brightcove Players defined above it to be created as soon\nas the line is read by the browser. If you wish to have the player instantiated only after\nthe rest of the HTML is processed and the page load is complete, remove the line.\n-->\n<script type="text/javascript">brightcove.createExperiences();</script>\n\n<!-- End of Brightcove Player -->');
