INSERT INTO `resources` VALUES(177, 'How Stereotypes Affect Us and What We Can Do - Claude Steele', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=2817651938001', '2817651938001', 'Claude Steele is the Dean of the Stanford School of Education and a social psychologist by training. In this video he explains the nature of stereotype threat and how it can influence our behavior and affect our performance on tests, in social situations and in all areas of our lives.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\n\n<div style="display:none">\n\n</div>\n\n<!--\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \n-->\n\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\n\n<object id="myExperience2817651938001 " class="BrightcoveExperience">\n  <param name="bgcolor" value="#FFFFFF" />\n  <param name="width" value="560" />\n  <param name="height" value="315" />\n  <param name="playerID" value="2817651938001 " />\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\n  <param name="isVid" value="true" />\n  <param name="isUI" value="true" />\n  <param name="dynamicStreaming" value="true" />\n  \n  <param name="@videoPlayer" value="2817651938001 " />\n</object>\n\n<!-- \nThis script tag will cause the Brightcove Players defined above it to be created as soon\nas the line is read by the browser. If you wish to have the player instantiated only after\nthe rest of the HTML is processed and the page load is complete, remove the line.\n-->\n<script type="text/javascript">brightcove.createExperiences();</script>\n\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(178, 'Educator Steve Cohen: We and They', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=3642380484001', '3642380484001', 'Steve Cohen, Senior Lecturer at Tufts UniversityÃ¢Â€Â™s Department of Education, describes how Facing History and OurselvesÃ¢Â€Â™ curriculum enables students to examine and explore the labels that humans place on one another. Cohen explains that we understand the world by categorizing people, often into groups such as Ã¢Â€ÂœweÃ¢Â€Â and Ã¢Â€Âœthey.Ã¢Â€Â He demonstrates how Facing History students are taught to look carefully at their use of words, helping them to deeply understand what words mean and that they can have different connotations, depending on their context. This video is part of the Facing History Best Practices video series.', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\n\n<div style="display:none">\n\n</div>\n\n<!--\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \n-->\n\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\n\n<object id="myExperience3642380484001 " class="BrightcoveExperience">\n  <param name="bgcolor" value="#FFFFFF" />\n  <param name="width" value="560" />\n  <param name="height" value="315" />\n  <param name="playerID" value="3642380484001 " />\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\n  <param name="isVid" value="true" />\n  <param name="isUI" value="true" />\n  <param name="dynamicStreaming" value="true" />\n  \n  <param name="@videoPlayer" value="3642380484001 " />\n</object>\n\n<!-- \nThis script tag will cause the Brightcove Players defined above it to be created as soon\nas the line is read by the browser. If you wish to have the player instantiated only after\nthe rest of the HTML is processed and the page load is complete, remove the line.\n-->\n<script type="text/javascript">brightcove.createExperiences();</script>\n\n<!-- End of Brightcove Player -->');
INSERT INTO `resources` VALUES(179, '"What Do We Do With A Variation?"', 10, '', '', '<html>\r\n<head>\r\n</head>\r\n<body>\r\n<div id="variation">\r\n<h1 style="text-align: center;">What Do We Do with a Variation?</h1>\r\n<h2 style="text-align: center;"><span style="font-size: small;">By James Berry</span></h2>\r\n<p></p>\r\n<table align="center" border="0">\r\n<tbody>\r\n<tr>\r\n<td><span style="font-size: medium;">What do we do with a difference?</span><br /><span style="font-size: medium;">Do we stand and discuss its oddity</span><br /><span style="font-size: medium;">or do we ignore it?</span><br /><span style="font-size: medium;">Do we shut our eyes to it</span><br /><span style="font-size: medium;">or poke it with a stick?</span><br /><span style="font-size: medium;">Do we clobber it to death?</span><br /><span style="font-size: medium;">Do we move around it in rage</span><br /><span style="font-size: medium;">and enlist the rage of others?</span><br /><span style="font-size: medium;">Do we will it to go away?</span><br /><span style="font-size: medium;">Do we look at it in awe</span><br /><span style="font-size: medium;">or purely in wonderment?</span><br /><span style="font-size: medium;">Do we work for it to disappear?</span><br /><span style="font-size: medium;">Do we pass it stealthily</span><br /><span style="font-size: medium;">Or change route away from it?</span><br /><span style="font-size: medium;">Do we will it to become like ourselves?</span><br /><span style="font-size: medium;">What do we do with a difference?</span><br /><span style="font-size: medium;">Do we communicate to it,</span><br /><span style="font-size: medium;">let application acknowledge it</span><br /><span style="font-size: medium;">for barriers to fall down?</span><br /><br /></td>\r\n<td>\r\n<p><img src="http://upload.wikimedia.org/wikipedia/commons/thumb/0/00/Diversity-Discrimination-05.jpg/320px-Diversity-Discrimination-05.jpg" style="margin-left: 20px; margin-right: 20px; vertical-align: text-top;" class="choicesshadow"/></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<hr /></div>\r\n</body>', '', NULL);
INSERT INTO `resources` VALUES(180, 'Ranking Humankind', 10, 'https://www.facinghistory.org/for-educators/educator-resources/readings/ranking-humankind', '', 'This outlines eighteenth century efforts to classify living beings. Notice how the chart by Petrus Camper shows a progression of races and the ways this could be used to justify slavery. Two centuries later, scientists have discredited the belief that there are biological differences among races', '', NULL);
INSERT INTO `resources` VALUES(181, 'Race and Racism', 10, 'https://www.facinghistory.org/for-educators/educator-resources/readings/race-and-racism', '', 'Scientists say that race is a meaningless idea. Can a meaningless idea affect the way people act? Sociologists say it can', '', NULL);
INSERT INTO `resources` VALUES(182, 'The "In" Group (Optional)', 10, 'https://www.facinghistory.org/for-educators/educator-resources/readings/group', '', '', '', NULL);
INSERT INTO `resources` VALUES(183, 'Not In Our Town: New Immigrants Share Their Stories', 11, 'http://www.niot.org/nios-video/new-immigrants-share-their-stories', '', 'The students of Newcomers High School in Long Island City, which specializes in teaching recent immigrants, and those of St. Luke''s, a private middle school in Manhattan, have come together to dialogue about difference and combat bias. ', '', NULL);
INSERT INTO `resources` VALUES(184, 'Not In Our Town: What Do you Say To "That''s So Gay"?', 11, 'http://www.niot.org/nios-video/what-do-you-say-quotthat039s-so-gayquot', '', 'In a lively class discussion, Gunn High School students in Roni Habib''s Facing History and Ourselves class challenge the saying "That''s so gay." This is a phrase that many students hear constantly and watching this film can inspire the viewers to speak up next time they hear it.', '', NULL);
INSERT INTO `resources` VALUES(185, 'Not In Our Town: Spread the Peace', 11, 'http://www.niot.org/nios/lesson/facing-history-and-ourselves-lesson-plan-spread-peace', '', 'The short video, â€œSpread the Peace,â€ produced by Not in Our School, shows how students at Mother Caroline Academy (Dorcester, Massachusetts) responded to growing violence in their community. ', '', NULL);
INSERT INTO `resources` VALUES(186, 'Not In Our Town: Art Students Picture a World Without Intolerance', 11, 'http://www.niot.org/nios-video/art-students-picture-world-without-intolerance', '', 'In this excerpt of "Not In Our School Palo Alto," Palo Alto High School students in Margo Wixsom''s art classes design compelling posters about confronting intolerance and stereotyping. ', '', NULL);
INSERT INTO `resources` VALUES(187, 'Congressman John Lewis on the Civil Rights Movement', 11, 'http://video.facinghistory.org/services/player/bcpid2083241027001?bckey=AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-&bctid=3024541425001', '3024541425001', 'Forty years ago Congressman John Lewis could never have imagined that he could become a member of the House of Representatives.  He speaks of the lessons of the civil rights movement: how Americans must come together "to create one house, one family" because "history teaches us we are all in the same boat."\r\n<br/>\r\n<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience3024541425001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="3024541425001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="3024541425001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->', 'Imported From Brightcove', '<!-- Start of Brightcove Player -->\r\n\r\n<div style="display:none">\r\n\r\n</div>\r\n\r\n<!--\r\nBy use of this code snippet, I agree to the Brightcove Publisher T and C \r\nfound at https://accounts.brightcove.com/en/terms-and-conditions/. \r\n-->\r\n\r\n<script language="JavaScript" type="text/javascript" src="http://admin.brightcove.com/js/BrightcoveExperiences.js"></script>\r\n\r\n<object id="myExperience3024541425001 " class="BrightcoveExperience">\r\n  <param name="bgcolor" value="#FFFFFF" />\r\n  <param name="width" value="560" />\r\n  <param name="height" value="315" />\r\n  <param name="playerID" value="3024541425001 " />\r\n  <param name="playerKey" value="AQ~~,AAABYEdBaAk~,1dGnVytVBSw9Y4yoko71XnFgkvpqea1-" />\r\n  <param name="isVid" value="true" />\r\n  <param name="isUI" value="true" />\r\n  <param name="dynamicStreaming" value="true" />\r\n  \r\n  <param name="@videoPlayer" value="3024541425001 " />\r\n</object>\r\n\r\n<!-- \r\nThis script tag will cause the Brightcove Players defined above it to be created as soon\r\nas the line is read by the browser. If you wish to have the player instantiated only after\r\nthe rest of the HTML is processed and the page load is complete, remove the line.\r\n-->\r\n<script type="text/javascript">brightcove.createExperiences();</script>\r\n\r\n<!-- End of Brightcove Player -->');
